function getCurrentDate(){const currentDate=new Date();const year=currentDate.getFullYear();const month=String(currentDate.getMonth()+1).padStart(2,'0');const day=String(currentDate.getDate()).padStart(2,'0');return `${year}-${month}-${day}`}
function getRandomInteger(min,max){return Math.floor(Math.random()*(max-min+1))+min}
function shuffleArray(array){for(let i=array.length-1;i>0;i--){const j=Math.floor(Math.random()*(i+1));[array[i],array[j]]=[array[j],array[i]]}
return array}
function capitalizeWords(string){return string.replace(/\b\w/g,(match)=>match.toUpperCase())}
function formatNumberWithCommas(number){return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g,',')}
function isPalindrome(string){const reversedString=string.split('').reverse().join('');return string===reversedString}
function deepClone(object){return JSON.parse(JSON.stringify(object))}
function isEven(number){return number%2===0}
function isNumeric(string){return/^\d+$/.test(string)}
function getCurrentTime(){const currentTime=new Date();const hours=String(currentTime.getHours()).padStart(2,'0');const minutes=String(currentTime.getMinutes()).padStart(2,'0');const seconds=String(currentTime.getSeconds()).padStart(2,'0');return `${hours}:${minutes}:${seconds}`}
function toLowerCase(string){return string.toLocaleLowerCase()}
function truncateString(string,maxLength){if(string.length<=maxLength){return string}
return string.substring(0,maxLength)+'...'}
function factorial(number){if(number===0||number===1){return 1}
return number*factorial(number-1)}
function hasDuplicates(array){return new Set(array).size!==array.length}
function trimString(string){return string.trim()}
function slugify(string){return string.toLowerCase().replace(/\s+/g,'-')}
function calculateSum(array){return array.reduce((accumulator,currentValue)=>accumulator+currentValue,0)}
function generateRandomString(length){const characters='abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';let randomString='';for(let i=0;i<length;i++){randomString+=characters.charAt(Math.floor(Math.random()*characters.length))}
return randomString}
function isEmptyString(string){return string.trim()===''}
function isPrime(number){if(number<=1){return!1}
for(let i=2;i<=Math.sqrt(number);i++){if(number%i===0){return!1}}
return!0}
function getCurrentTimestamp(){return Date.now()}
function isEmptyObject(object){return Object.keys(object).length===0}
function toUpperCase(string){return string.toLocaleUpperCase()}
function calculateAverage(array){if(array.length===0){return 0}
const sum=array.reduce((accumulator,currentValue)=>accumulator+currentValue,0);return sum/array.length}
function getCurrentYear(){return new Date().getFullYear()}
function removeElementFromArray(array,element){const index=array.indexOf(element);if(index!==-1){array.splice(index,1)}
return array}
function getFileExtension(filePath){return filePath.slice(filePath.lastIndexOf('.')+1)}
function startsWith(string,substring){return string.startsWith(substring)}
function reverseString(string){return string.split('').reverse().join('')}
function isSorted(array){for(let i=1;i<array.length;i++){if(array[i]<array[i-1]){return!1}}
return!0}
function objectToArray(object){return Object.entries(object)}
function getCurrentDayOfWeek(){const daysOfWeek=['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];const currentDayIndex=new Date().getDay();return daysOfWeek[currentDayIndex]}
function removeDuplicates(array){return[...new Set(array)]}
function endsWith(string,substring){return string.endsWith(substring)}
function exponentiation(base,exponent){return base**exponent}
function toTitleCase(string){return string.toLowerCase().replace(/\b\w/g,(match)=>match.toUpperCase())}
function calculateProduct(array){return array.reduce((accumulator,currentValue)=>accumulator*currentValue,1)}
function isEmptyArray(array){return array.length===0}
function padNumberWithZeros(number,width){return String(number).padStart(width,'0')}
function findMax(array){return Math.max(...array)}
function arraysAreEqual(array1,array2){if(array1.length!==array2.length){return!1}
for(let i=0;i<array1.length;i++){if(array1[i]!==array2[i]){return!1}}
return!0}
function toCamelCase(string){return string.replace(/[-_](.)/g,(_,char)=>char.toUpperCase())}
function getCurrentMonth(){const months=['January','February','March','April','May','June','July','August','September','October','November','December'];const currentMonthIndex=new Date().getMonth();return months[currentMonthIndex]}
function calculateMedian(array){if(array.length===0){return 0}
const sortedArray=array.slice().sort((a,b)=>a-b);const middleIndex=Math.floor(sortedArray.length/2);if(sortedArray.length%2===0){return(sortedArray[middleIndex-1]+sortedArray[middleIndex])/2}else{return sortedArray[middleIndex]}}
function arrayContains(array,element){return array.includes(element)}
function toKebabCase(string){return string.replace(/\s+/g,'-').toLowerCase()}
function objectsAreEqual(object1,object2){return JSON.stringify(object1)===JSON.stringify(object2)}
function isLeapYear(year){return(year%4===0&&year%100!==0)||year%400===0}
function isValidEmail(email){const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return emailRegex.test(email)}
function getCurrentTimeShort(){const currentTime=new Date();const hours=String(currentTime.getHours()).padStart(2,'0');const minutes=String(currentTime.getMinutes()).padStart(2,'0');return `${hours}:${minutes}`}
function numberToStringWithTrailingZeros(number,decimals){return number.toFixed(decimals)}
function arrayIncludes(array,value){return array.includes(value)}
function getCurrentTimestampInSeconds(){return Math.floor(Date.now()/1000)}
function removeElementFromArrayByIndex(array,index){if(index>=0&&index<array.length){array.splice(index,1)}
return array}
function getRandomArrayItem(array){const randomIndex=Math.floor(Math.random()*array.length);return array[randomIndex]}
function formatDuration(durationInSeconds){const hours=Math.floor(durationInSeconds/3600);const minutes=Math.floor((durationInSeconds%3600)/60);const seconds=durationInSeconds%60;return `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`}
function toSnakeCase(string){return string.replace(/\s+/g,'_').toLowerCase()}
function isSubset(array,subset){return subset.every((value)=>array.includes(value))}
function generateRandomHexColor(){const letters='0123456789ABCDEF';let color='#';for(let i=0;i<6;i++){color+=letters[Math.floor(Math.random()*16)]}
return color}
function calculateDistance(x1,y1,x2,y2){const xDiff=x2-x1;const yDiff=y2-y1;return Math.sqrt(xDiff**2+yDiff**2)}
function splitIntoWords(string){return string.split(/\s+/)}
function groupBy(array,key){return array.reduce((result,item)=>{const groupKey=item[key];if(!result[groupKey]){result[groupKey]=[]}
result[groupKey].push(item);return result},{})}
function getRandomIntegerInRange(min,max){return Math.floor(Math.random()*(max-min+1))+min}
function removeFalsyValues(array){return array.filter(Boolean)}
function calculateDateDifferenceInDays(date1,date2){const timeDifference=Math.abs(date2.getTime()-date1.getTime());const daysDifference=Math.ceil(timeDifference/(1000*60*60*24));return daysDifference}
function removeDuplicateObjects(array,property){return array.filter((obj,index,self)=>index===self.findIndex((el)=>el[property]===obj[property]))}
function getCurrentURL(){return window.location.href}
function generateRandomID(){return Math.random().toString(36).substring(2)}
function sortByProperty(array,property){return array.sort((a,b)=>a[property]-b[property])}
function toPascalCase(string){return string.replace(/(\w)(\w*)/g,(_,firstChar,remainingChars)=>firstChar.toUpperCase()+remainingChars.toLowerCase())}
function isValuePresent(collection,value){if(Array.isArray(collection)){return collection.includes(value)}else if(typeof collection==='object'&&collection!==null){return Object.values(collection).includes(value)}
return!1}
function generateRandomIntegerArrayInRange(min,max,length){const array=[];for(let i=0;i<length;i++){array.push(Math.floor(Math.random()*(max-min+1))+min)}
return array}
function countOccurrences(array){return array.reduce((acc,val)=>{acc[val]=(acc[val]||0)+1;return acc},{})}
function removeHTMLTags(string){return string.replace(/<[^>]*>/g,'')}
function toSlug(string,separator='-'){return string.toLowerCase().replace(/[^a-z0-9]+/g,separator).replace(new RegExp(`^${separator}|${separator}$`,'g'),'')}
function isValidURL(value){try{new URL(value);return!0}catch(error){return!1}}
function objectToQueryString(object){const params=new URLSearchParams();for(const key in object){if(Object.hasOwnProperty.call(object,key)){params.append(key,object[key])}}
return params.toString()}
function findMinMax(array){const min=Math.min(...array);const max=Math.max(...array);return{min,max}}
function getBrowserInfo(){const userAgent=navigator.userAgent;let browserName;let browserVersion;if(userAgent.indexOf('Firefox')>-1){browserName='Mozilla Firefox';browserVersion=userAgent.match(/Firefox\/(\d+\.\d+)/)[1]}else if(userAgent.indexOf('Chrome')>-1){browserName='Google Chrome';browserVersion=userAgent.match(/Chrome\/(\d+\.\d+)/)[1]}else if(userAgent.indexOf('Safari')>-1){browserName='Safari';browserVersion=userAgent.match(/Safari\/(\d+\.\d+)/)[1]}else if(userAgent.indexOf('MSIE')>-1||userAgent.indexOf('Trident/')>-1){browserName='Internet Explorer';browserVersion=userAgent.match(/(?:MSIE |rv:)(\d+(\.\d+)?)/)[1]}else{browserName='Unknown';browserVersion='N/A'}
return{browserName,browserVersion}}
function isPalindromeIgnoreNonAlphanumeric(string){const formattedString=string.toLowerCase().replace(/[^a-zA-Z0-9]+/g,'');const reversedString=formattedString.split('').reverse().join('');return formattedString===reversedString}
function toRomanNumerals(number){const romanNumerals=[{value:1000,symbol:'M'},{value:900,symbol:'CM'},{value:500,symbol:'D'},{value:400,symbol:'CD'},{value:100,symbol:'C'},{value:90,symbol:'XC'},{value:50,symbol:'L'},{value:40,symbol:'XL'},{value:10,symbol:'X'},{value:9,symbol:'IX'},{value:5,symbol:'V'},{value:4,symbol:'IV'},{value:1,symbol:'I'}];let result='';for(const numeral of romanNumerals){while(number>=numeral.value){result+=numeral.symbol;number-=numeral.value}}
return result}
